import React, { Component } from 'react';
import { Form, Button } from 'react-bootstrap';
import { connect } from 'react-redux';
import { signInUser } from '../redux/actions';
import propTypes from 'prop-types';




class SignIn extends Component {

    state = {
        email: '',
        password: '',
        msg: null
    }
    static propTypes = {
        isAthenticated: propTypes.bool,
        error: propTypes.object.isRequired,
        signIn: propTypes.func.isRequired,
        clearErrors: propTypes.func.isRequired
    }

    componentDidUpdate(prevProps) {
        const { error } = this.props;
        if (error !== prevProps.error) {
            //Check for register error
            if (error.id === 'SIGNIN_FAIL') {
                this.setState({ msg: this.props.error.msg })
            } else { this.setState({ msg: null }) }
        }
    }




    // componentDidMount(){
    //     this.setState({
    //         username: '',
    //         password: ''
    //     })  
    // }

    onSubmitHandle = (event) => {
        event.preventDefault();
        fetch('http://localhost:3001/signin', {
            method: 'POST',
            headers: {
                'Content-type': 'application/json',
            },
            body: JSON.stringify(this.state)
        })
            .then(response => response.json())
            .then(user => {
                if (user.id) {
                    this.setState({ isSignedIn: true })
                    this.props.loadUser(user);
                }
            })




    }

    handleChange = (event) => {
        // const {value, name} = event.target;

        // dynamically set the state, using whatever is used for name eg username or password:
        //  [name] to value
        this.setState({ [event.target.name]: event.target.value })
        // this.setState({ [name]: value })
    }


    render() {

        return (
            <div>
                <Form>
                    <h2>Sign In</h2>
                    <Form.Group controlId="usernameFormGroup">
                        <Form.Label>Email</Form.Label>
                        <Form.Control
                            name="email"
                            type="text"
                            // value={this.state.email}
                            onChange={this.handleChange}
                            placeholder="Enter email" />
                    </Form.Group>

                    <Form.Group controlId="passwordFormGroup">
                        <Form.Label>Password</Form.Label>
                        <Form.Control
                            name="password"
                            type="password"
                            // value={this.state.password}
                            onChange={this.handleChange}
                            placeholder="Enter password" />
                    </Form.Group>

                    <Button
                        onClick={this.onSubmitHandle}
                        className="book-button"
                        variant="outline-light"
                        type="submit">
                        Submit
                </Button>
                </Form>
            </div>
        )
    }
}


const mapStateToProps = state => {
    return {
        isAuthenticated: state.auth.isAuthenticated,
        error: state.error
    }
}


const mapDispatchToProps = (dispatch) => {
    return {
        signIn: (user) => dispatch(signInUser(user))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(SignIn);
