import * as actionTypes from '../actions/constants';

const initialState = {
    currentUser: {},
    isPending: false,
    error: ''
}


const signinUser = (state = initialState, action = {}) => {
    switch (action.type) {
        case actionTypes.SIGNIN_USER_PENDING:
            return {
                ...state,
                currentUser: action.payload,
                isPending: true
            }
        case actionTypes.SIGNIN_USER_SUCCESS:

        default:
            return state;
    }
}

export default signinUser;
