import * as actionTypes from './constants'

export const listBooks = (books, language) => {
    return {
        type: actionTypes.LIST_BOOKS,
        books,
        language
    }
}


export const addBook = () => (dispatch) => {
    dispatch({ type: actionTypes.ADD_BOOK_PENDING })
    fetch('http://localhost:3001/add', {
        method: 'POST', // *GET, POST, PUT, DELETE, etc.
        headers: {
            'Content-Type': 'application/json'
            // 'Content-Type': 'application/x-www-form-urlencoded',
        },

        body: JSON.stringify(book) // body data type must match "Content-Type" header
    })
        .then(data => dispatch({ type: actionTypes.ADD_BOOK_SUCCES, payload: data }))
        .catch(error => dispatch({ type: actionTypes.ADD_BOOK_FAILED, payload: error }))

}

export const requestBooks = () => (dispatch) => {
    dispatch({ type: actionTypes.REQUEST_BOOKS_PENDING })
    fetch('http://localhost:3001')
        .then(response => response.json())
        .then(data => dispatch({ type: actionTypes.REQUEST_BOOKS_SUCCES, payload: data }))
        .catch(error => dispatch({ type: actionTypes.REQUEST_BOOKS_FAILED, payload: error }))

}
