import React from 'react';
import { Form, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';

class SignUp extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            name: '',
            username: '',
            email: '',
            password: ''
        }
    }

    onChangeInput = (event) => {
        this.setState({ [event.target.name]: event.target.value })
    }

    onSubmitHandle = (event) => {
        event.preventDefault()
        fetch('http://localhost:3001/signup', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(this.state)
        })
        // reroute to login page, does not work yet...
        // see:  https://stackoverflow.com/questions/48497510/simple-conditional-routing-in-reactjs
        // return <Redirect to={"/login" }
        // />

    }

    render() {
        return (
            <div>
                <Form>
                    <h2>Sign Up</h2>
                    <Form.Group controlId="nameText">
                        <Form.Label>Name</Form.Label>
                        <Form.Control
                            name="name"
                            value={this.state.name}
                            onChange={this.onChangeInput}
                            type="text"
                            placeholder="Enter name" />
                    </Form.Group>

                    <Form.Group controlId="usernameText">
                        <Form.Label>Username</Form.Label>
                        <Form.Control
                            name="username"
                            value={this.state.username}
                            onChange={this.onChangeInput}
                            type="text"
                            placeholder="Enter username" />
                    </Form.Group>

                    <Form.Group controlId="emailText">
                        <Form.Label>Email</Form.Label>
                        <Form.Control
                            name="email"
                            value={this.state.email}
                            onChange={this.onChangeInput}
                            type="text"
                            placeholder="Enter email" />
                    </Form.Group>

                    <Form.Group controlId="passwordText">
                        <Form.Label>Password</Form.Label>
                        <Form.Control
                            name="password"
                            value={this.state.password}
                            onChange={this.onChangeInput}
                            type="password"
                            placeholder="Enter password" />
                    </Form.Group>
                    <Link to={'/'}>
                        <Button
                            onClick={this.onSubmitHandle}
                            className="book-button"
                            variant="outline-light"
                            type="submit">
                            Submit
                        </Button>
                    </Link>
                </Form>
            </div>
        )
    }

}

export default SignUp;
