import React from 'react';
import { Form, Button } from 'react-bootstrap';
import { NavLink, Redirect } from 'react-router-dom';
import { connect } from 'react-redux';
import { signUpUser } from '../redux/actions/userActions';

class SignUp extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            name: '',
            username: '',
            email: '',
            password: ''
        }
    }

    onChangeInput = (event) => {
        this.setState({ [event.target.name]: event.target.value })
    }

    onSubmitHandle = (event) => {
        event.preventDefault()
        this.props.onSubmitHandle(this.state)
        return <Redirect to={"/"} />
    }

    render() {
        // console.log("state in SignUp: ", this.props)
        return (
            <div>
                <Form>
                    <h2>Sign Up</h2>
                    <Form.Group controlId="nameText">
                        <Form.Label>Name</Form.Label>
                        <Form.Control
                            name="name"
                            value={this.state.name}
                            onChange={this.onChangeInput}
                            type="text"
                            placeholder="Enter name" />
                    </Form.Group>

                    <Form.Group controlId="usernameText">
                        <Form.Label>Username</Form.Label>
                        <Form.Control
                            name="username"
                            value={this.state.username}
                            onChange={this.onChangeInput}
                            type="text"
                            placeholder="Enter username" />
                    </Form.Group>

                    <Form.Group controlId="emailText">
                        <Form.Label>Email</Form.Label>
                        <Form.Control
                            name="email"
                            value={this.state.email}
                            onChange={this.onChangeInput}
                            type="text"
                            placeholder="Enter email" />
                    </Form.Group>

                    <Form.Group controlId="passwordText">
                        <Form.Label>Password</Form.Label>
                        <Form.Control
                            name="password"
                            value={this.state.password}
                            onChange={this.onChangeInput}
                            type="password"
                            placeholder="Enter password" />
                    </Form.Group>

                    <Button
                        onClick={this.onSubmitHandle}
                        className="book-button"
                        variant="outline-light"
                        type="submit">
                        <NavLink to={"/"}>
                            Submit
                            </NavLink>
                    </Button>
                </Form>
            </div>
        )
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        onSubmitHandle: (user) => dispatch(signUpUser(user))
    }
}

const mapStateToProps = (state) => {
    return {
        currentUser: state.signUpUser.currentUser
    }

}

export default connect(mapStateToProps, mapDispatchToProps)(SignUp);
