import React,{Component} from 'react';
import {NavLink, Redirect} from 'react-router-dom';


// import '../App.css';

class  Home  extends Component{
    constructor(props) {
        super(props)
        console.log(this.props)
        this.state = {
            isSignedIn: this.props.location.state ? this.props.location.state.isSignedIn: false,
            user: {
                id: '',
                name: '',
                username: '',
                email:''
            } 
        }
    }

    componentDidMount() {
        if(this.props.location.state){
            this.setState({ 
                isSignedIn: this.props.location.state.isSignedIn
            })

        }
        console.log('state after componentDidMount:', this.state)
    }

    render() {
        if(this.state.isSignedIn !== true ){
            return <Redirect to={'/login'} />
        } else{
            return(
                <div>
                    <div className="section-holder">
                        <NavLink className='main-section' language={"dutch"} to='/dutch'>dutch</NavLink>
                        <NavLink className='main-section' language={"english"} to='/english'>english</NavLink>
                        <NavLink className='main-section' language={"arabic"} to='/arabic'>arabic</NavLink>   
                    </div>
                    <div className="section-holder"><NavLink className='main-section' to='/add'>Add Book</NavLink></div>
                </div>
            )
        }

    }
}


export default Home;
