import React, { Component } from 'react';
import { connect } from 'react-redux';
import { listBooks } from '../redux/reducers/bookReducer'

import { NavLink } from 'react-router-dom';
import Card from 'react-bootstrap/Card';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

class Book extends Component {
    constructor(props) {
        super(props)
        this.state = {
            books: []
        }
    }
    // book={book} to={'/edit/' + book.id}

    // componentDidMount(){
    //     fetch('http://localhost:3001')
    //     .then(response => response.json())
    //     .then( data => {
    //         // console.log(data)
    //         this.setState({
    //             books: [ ...data]
    //         })  
    //     })
    // }


    render() {
        console.log(this.props)
        return (
            <Container>
                {
                    this.props.books.map((book, i) => {
                        return book.language === this.props.language ?
                            <Row key={i}>
                                <Col lg={4} sm={6}>

                                    <Card >
                                        <Card.Body>
                                            <Card.Text >
                                                {book.title}
                                            </Card.Text>
                                        </Card.Body>
                                        <NavLink to={{
                                            pathname: `/edit/${book.id}`,
                                            bookProps: book
                                        }}>
                                            <Card.Img variant="bottom" src={`../img/${book.img}`} />
                                        </NavLink>
                                    </Card>
                                </Col>
                            </Row>
                            : '';
                    })
                }
            </Container>
        )   // closing return function
    } // closing render function

} // closing Book class

const mapStateToProps = state => {
    return {
        books: state.library.books
    }
}

const mapDispatchToProps = dispatch => {
    return {
        listBooks: (books, language) => dispatch(listBooks(books, language))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Book);
