import * as actionTypes from './constants'

export const listBooks = (books, language) => {
    return {
        type: actionTypes.LIST_BOOKS,
        books,
        language
    }
}


export const addBook = (book) => ({
    type: actionTypes.ADD_BOOK,
    payload: {
        book: book
    }
})

export const requestBooks = () => (dispatch) => {
    dispatch({ type: actionTypes.REQUEST_BOOKS_PENDING })
    fetch('http://localhost:3001')
        .then(response => response.json())
        .then(data => dispatch({ type: actionTypes.REQUEST_BOOKS_SUCCES, payload: data }))
        .catch(error => dispatch({ type: actionTypes.REQUEST_BOOKS_FAILED, payload: error }))
}
