import React,{Component} from 'react';
import {Form, Button} from 'react-bootstrap';
import {NavLink} from 'react-router-dom';




class AddBook extends Component {
    constructor(props){
        super(props);
        this.state = {
            title : '',
            author: '',
            lang  : '',
            isread : '',
            image  : ''
        }
    }
    onSubmitHandle = (event) =>{
        event.preventDefault()  
        fetch('http://localhost:3001/add', {
            method: 'POST', // *GET, POST, PUT, DELETE, etc.
            headers: {
              'Content-Type': 'application/json'
              // 'Content-Type': 'application/x-www-form-urlencoded',
            },

            body: JSON.stringify(this.state) // body data type must match "Content-Type" header
          });


    }

    onTitleChange = (event) => {
        this.setState({title: event.target.value});
    }
    onAuthorChange = (event) => {
        this.setState({author: event.target.value});
    }

    onImageChange = (event) => {
        this.setState({image: event.target.value});
    }

    onLanguageChange = (event) => {
        this.setState({lang: event.target.value});
    }

    onIsReadChange = (event) => {
        this.setState({isread: event.target.value});
    }

    render(){
        return(
            <div>
                <div>ADD BOOK</div>
                <Form>
                    <Form.Group controlId="formBasicText">
                        <Form.Label>Title</Form.Label>
                        <Form.Control onChange={this.onTitleChange}type="text" placeholder="Enter title" />
                    </Form.Group>

                    <Form.Group onChange={this.onAuthorChange} controlId="formBasicText">
                        <Form.Label>Author</Form.Label>
                        <Form.Control type="text" placeholder="Enter Author" />
                    </Form.Group>

                    <Form.Group controlId="formBasicText">
                        <Form.Label>Image</Form.Label>
                        <Form.Control onChange={this.onImageChange}type="text" placeholder="Enter Image Name eg image.jpg" />
                    </Form.Group>

                    <Form.Group controlId="formBasicText">
                        <Form.Label>Language</Form.Label>
                        <Form.Control  onChange={this.onLanguageChange}type="text" placeholder="Enter Language" />
                    </Form.Group>

                    <Form.Group controlId="formBasicText">
                        <Form.Label>IsRead</Form.Label>
                        <Form.Control  onChange={this.onIsReadChange}type="text" placeholder="Have you read it?" />
                    </Form.Group>

                  <Button 
                    onClick={this.onSubmitHandle}
                    className="book-button" 
                    variant="outline-light" 
                    type="submit"> 
                     <NavLink to={'/add' }> 
                        Submit
                     </NavLink>
                  </Button>
                </Form>
            </div>
        )
    }

}

export default AddBook;
