import {
    USER_LOADING,
    USER_LOADED,
    AUTH_ERR,
    SIGNIN_USER_SUCCESS,
    SIGNIN_USER_FAILED,
    LOGOUT_SUCCES,
    SIGNUP_USER_SUCCESS,
    SIGNUP_USER_FAILED
} from '../actions/types';

const initialState = {
    token: localStorage.getItem('token'),
    isAuthenticated: null,
    isLoading: false,
    user: null
}

export default function (state = initialState, action) {
    switch (action.type) {
        case USER_LOADING:
            return {
                ...state,
                isLoading: true
            };
    }

}
