import React from 'react';
import { Form, Button, Alert } from 'react-bootstrap';
import { NavLink, Redirect } from 'react-router-dom';
import { connect } from 'react-redux';
import { signUpUser } from '../redux/actions/userActions';

import PropTypes from 'prop-types';

class SignUp extends React.Component {
    // super(props)
    state = {
        name: '',
        username: '',
        email: '',
        password: '',
        msg: null
    };

    static PropsTypes = {
        isAuthenticated: PropTypes.bool,
        error: PropTypes.object.isRequired,
        signUpUser: PropTypes.func.isRequired
    }

    componentDidUpdate(prevProps) {
        const { error } = this.props;
        if (error !== prevProps.error) {
            //Check for registor error
            if (error.id === 'REGISTER_FAIL') {
                this.setState({ msg: error.msg })
            } else { this.setState({ msg: null }) }
        }
    }

    onChangeInput = (event) => {
        this.setState({ [event.target.name]: event.target.value })
    }

    onSubmitHandle = (event) => {
        event.preventDefault()

        const { name, username, email, password } = this.state;

        const newUser = {
            name,
            username,
            email,
            password
        }
        this.props.onSubmitHandle(newUser)
        return <Redirect to={"/"} />
    }

    render() {
        return (
            <div>
                {this.state.msg ? <Alert variant="danger">{this.state.msg}</Alert> : null}
                < Form >
                    <h2>Sign Up</h2>
                    <Form.Group controlId="nameText">
                        <Form.Label>Name</Form.Label>
                        <Form.Control
                            name="name"
                            // value={this.state.name}
                            onChange={this.onChangeInput}
                            type="text"
                            placeholder="Enter name" />
                    </Form.Group>

                    <Form.Group controlId="usernameText">
                        <Form.Label>Username</Form.Label>
                        <Form.Control
                            name="username"
                            // value={this.state.username}
                            onChange={this.onChangeInput}
                            type="text"
                            placeholder="Enter username" />
                    </Form.Group>

                    <Form.Group controlId="emailText">
                        <Form.Label>Email</Form.Label>
                        <Form.Control
                            name="email"
                            // value={this.state.email}
                            onChange={this.onChangeInput}
                            type="text"
                            placeholder="Enter email" />
                    </Form.Group>

                    <Form.Group controlId="passwordText">
                        <Form.Label>Password</Form.Label>
                        <Form.Control
                            name="password"
                            // value={this.state.password}
                            onChange={this.onChangeInput}
                            type="password"
                            placeholder="Enter password" />
                    </Form.Group>

                    <Button
                        onClick={this.onSubmitHandle}
                        className="book-button"
                        variant="outline-light"
                        type="submit">
                        <NavLink to={"/"}>
                            Submit
                            </NavLink>
                    </Button>
                </Form >
            </div >
        )
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        onSubmitHandle: (user) => dispatch(signUpUser(user))
    }
}

const mapStateToProps = (state) => {
    return {
        isAuthanticated: state.auth.isAuthanticated,
        error: state.error
    }

}

export default connect(mapStateToProps, mapDispatchToProps)(SignUp);
