import {
    SIGNUP_USER_FAILED,
    SIGNUP_USER_PENDING,
    SIGNUP_USER_SUCCESS,
} from '../actions/types';

const initialState = {
    currentUser: {},
    isPending: false,
    error: ''
}


export const signUpUser = (state = initialState, action = {}) => {
    switch (action.type) {

        case SIGNUP_USER_PENDING:
            return Object.assign({}, state, { isPending: true })
        case SIGNUP_USER_SUCCESS:
            // console.log('actionPayload in signupUser:', action.payload)
            return Object.assign({}, state, { currentUser: action.payload, isPending: false })
        case SIGNUP_USER_FAILED:
            return Object.assign({}, state, { error: action.payload, isPending: false })
        default:
            return state;
    }
}

export const signOutUser = (state = initialState, action) => {

    if (action.type === SIGNOUT_USER) {

        return Object.assign({}, state, { currentUser: {} }, () => {
            console.log("Initialstate in signOutUser", state)
        })
    }
    else return state

}
