import React, {Component} from 'react';
import {Route, Switch} from 'react-router-dom';

import Home from '../components/Home';
import EnglishLibrary from '../components/EnglishLibrary';
import ArabicLibrary  from '../components/ArabicLibrary';
import DutchLibrary   from '../components/DutchLibrary';
import AddBook       from '../components/AddBook';
import EditBook     from '../components/EditBook';
import Login        from '../components/Login';
import SignUp        from '../components/SignUp';
import NavBar       from '../components/NavBar';
// import Signout      from './component/Signout';



import './App.css';

// initial state used when signing out
const initialState = {
  isSignedIn: false,
  name: '',
  username: '',
  email:''
}


class App extends Component {
  constructor(props){
    super(props)

    this.state = {
      isSignedIn: false,
      user: {
        id: '',
        name: '',
        username: '',
        email:''
      } 
    }
  }

  resetData = () => {
    this.setState(initialState)
  }

  loadUser = (data) => {
    this.setState({ user: {
      id: data.id,
      name: data.name,
      username: data.username,
      email: data.email1
    }

    })
  }

  render() {
    return (

      <div className="App">
        <NavBar resetData={this.resetData}/>
        <h1>BIBLIO</h1>
        <div >

          <Switch>
            <Route exact path='/'  render={(props) => <Home {...props} state={this.state} />} />
            <Route exact path='/english' component={EnglishLibrary}/>
            <Route exact path='/arabic' component={ArabicLibrary}/>
            <Route exact path='/dutch' component={DutchLibrary}/>
            <Route exact path='/add' component={AddBook}/>
            <Route exact path='/login' render={(props) => <Login {...props} loadUser={this.loadUser} />} />
            <Route exact path='/signin' component={SignUp}/>
            {/* <Route exact path='/signout' render={(props) => <Signout {...props} initialstate={this.initialState} />} /> */}
            <Route exact path='/edit/:id' component={EditBook}/>
          </Switch>
        </div>
      </div>
    );
  }
}


export default App;
