import React, { Component } from 'react';
import { Navbar } from 'react-bootstrap';
import { NavLink, Link } from 'react-router-dom';
import { signOutUser } from '../redux/actions/userActions';

import { connect } from 'react-redux';


class NavBar extends Component {

    signOut = (e) => {
        // console.log("signOut Werkt!, dit zjin dee props: ", this.props)
        event.preventDefault()
        this.props.signOut()
    }
    render() {
        console.log("props in NavBar", this.props)
        return (
            <div>
                <Navbar fixed="top" className="nav" expand="lg">
                    <Link to="/"><Navbar.Brand>BIBLIO</Navbar.Brand></Link>
                    <Navbar.Toggle aria-controls="basic-navbar-nav" />
                    <Navbar.Collapse id="basic-navbar-nav">
                        {/* <Form inline>
                        <FormControl  type="text" placeholder="Search" className="search-input mr-sm-2" />
                        <Button className="search-button" variant="outline">Search</Button>
                        </Form> */}
                        {
                            (this.props.currentUser.id)
                                ?
                                <div>
                                    <NavLink className="navLink" onClick={this.signOut} to="/">Sign Out</NavLink>
                                    <NavLink className="navLink" to='#'> {this.props.currentUser.username} </NavLink>
                                </div>
                                :
                                <div>
                                    <NavLink className="navLink" to="/signup">Sign Up</NavLink>
                                    <NavLink className="navLink" to="/signin">Sign In</NavLink>
                                </div>
                        }
                    </Navbar.Collapse>
                </Navbar>
            </div>
        )
    }
}
const mapDispatchToProps = (dispatch) => {
    return {
        signOut: () => dispatch(signOutUser)
    }

}

const mapStateToProps = state => ({
    currentUser: state.signUpUser.currentUser
})

export default connect(mapStateToProps, mapDispatchToProps)(NavBar);    // higher order component
