import React, { Component } from 'react';
import { connect } from 'react-redux';
import { NavLink } from 'react-router-dom';
import Card from 'react-bootstrap/Card';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import { requestBooks } from '../redux/actions/libraryActions'

class Book extends Component {
    constructor(props) {
        super(props)
        this.state = {
            books: []
        }
    }
    // book={book} to={'/edit/' + book.id}

    componentDidMount() {
        this.props.onRequestBooks()
    }


    render() {
        console.log(this.props)
        return (
            <Container>
                {
                    this.props.books.map((book, i) => {
                        return book.language === this.props.language ?
                            <Row key={i}>
                                <Col lg={4} sm={6}>
                                    <Card >
                                        <Card.Body>
                                            <Card.Text >
                                                {book.title}
                                            </Card.Text>
                                        </Card.Body>
                                        <NavLink to={{
                                            pathname: `/edit/${book.id}`,
                                            bookProps: book
                                        }}>
                                            <Card.Img variant="bottom" src={`../img/${book.img}`} />
                                        </NavLink>
                                    </Card>
                                </Col>
                            </Row>
                            : '';
                    })
                }
            </Container>
        )   // closing return function
    } // closing render function

} // closing Book class

const mapStateToProps = state => {
    return {
        books: state.requestBooks.books,
        isPending: state.requestBooks.isPending,
        error: state.requestBooks.error
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        onRequestBooks: () => dispatch(requestBooks())
    }

}

export default connect(mapStateToProps, mapDispatchToProps)(Book);
