// AUTH REDUCER
import {
    USER_LOADING,
    USER_LOADED,
    AUTH_ERROR,
    SIGNIN_USER_SUCCESS,
    SIGNIN_USER_FAILED,
    LOGOUT_SUCCES,
    SIGNUP_USER_SUCCESS,
    SIGNUP_USER_FAILED
} from '../actions/types';

const initialState = {
    token: localStorage.getItem('token'),
    isAuthenticated: null,
    isLoading: false,
    user: null
}

export default function (state = initialState, action) {
    switch (action.type) {
        case USER_LOADING:
            return {
                ...state,
                isLoading: true
            };
        case USER_LOADED:
            return {
                ...state,
                isAuthenticated: true,
                isLoading: false,
                user: action.payload
            };
        case SIGNIN_USER_SUCCESS:
        case SIGNUP_USER_SUCCESS:
            return {
                ...state,
                ...action.payload,  // the user and the token
                isAuthenticated: true,
                isLoading: false,
            }
        case AUTH_ERROR:
        case SIGNUP_USER_FAILED:
        case LOGOUT_SUCCES:
        case SIGNUP_USER_FAILED:
            return {
                ...state,
                token: null,
                user: null,
                isAuthenticated: false,
                isLoading: false,
            }
        default:
            return state;
    }

}
