import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import { Form, Button } from 'react-bootstrap';


class EditBook extends Component {
    constructor(props) {
        super(props)
        this.state = {
            id: this.props.match.params.id,
            title: '',
            author: '',
            image: '',
            lang: '',
            isread: ''
        }
    }


    componentDidMount() {
        fetch('http://localhost:3001/')
            .then(response => response.json())
            .then(data => {
                data.map((bookItem, i) => {
                    if (bookItem.id === Number(this.props.match.params.id)) {
                        this.setState({
                            title: bookItem.title,
                            author: bookItem.author,
                            image: bookItem.image,
                            lang: bookItem.lang,
                            isread: bookItem.isread
                        })
                    }
                    return bookItem
                })
            })
    }


    onSubmitHandle = (event) => {
        event.preventDefault()
        fetch('http://localhost:3001/edit', {
            method: 'PUT', // *GET, POST, PUT, DELETE, etc.
            headers: {
                'Content-Type': 'application/json'
                // 'Content-Type': 'application/x-www-form-urlencoded',
            },

            body: JSON.stringify(this.state) // body data type must match "Content-Type" header
        });
    }

    onTitleChange = (event) => {
        // [e.target.name]: e.target.value
        this.setState({ title: event.target.value });
    }
    onAuthorChange = (event) => {
        this.setState({ author: event.target.value });
    }

    onImageChange = (event) => {
        this.setState({ image: event.target.value });
    }

    onLanguageChange = (event) => {
        this.setState({ lang: event.target.value });
    }

    onIsReadChange = (event) => {
        this.setState({ isread: event.target.value });
    }


    render() {
        return (
            <div>EDIT
                <Form>
                    <Form.Group controlId="formBasicText">
                        <Form.Label>Title</Form.Label>
                        <Form.Control onChange={this.onTitleChange} type="text" defaultValue={this.state.title} />
                    </Form.Group>

                    <Form.Group onChange={this.onAuthorChange} controlId="formBasicText">
                        <Form.Label>Author</Form.Label>
                        <Form.Control type="text" defaultValue={this.state.author} />
                    </Form.Group>

                    <Form.Group controlId="formBasicText">
                        <Form.Label>Image</Form.Label>
                        <Form.Control onChange={this.onImageChange} type="text" defaultValue={this.state.image} />
                    </Form.Group>

                    <Form.Group controlId="formBasicText">
                        <Form.Label>Language</Form.Label>
                        <Form.Control onChange={this.onLanguageChange} type="text" defaultValue={this.state.lang} />
                    </Form.Group>

                    <Form.Group controlId="formBasicText">
                        <Form.Label>IsRead</Form.Label>
                        <Form.Control onChange={this.onIsReadChange} type="text" defaultValue={this.state.isread} />
                    </Form.Group>

                    <Button
                        onClick={this.onSubmitHandle}
                        className="book-button"
                        variant="outline-light"
                        type="submit">
                        <NavLink to={`/${this.state.lang}`}>
                            Submit
                     </NavLink>
                    </Button>
                    <Button

                        className="book-button"
                        variant="outline-light"
                        type="submit">
                        <NavLink to={`/${this.state.lang}`}>
                            Cancel
                     </NavLink>
                    </Button>
                </Form>
            </div>
        )
    }
}


export default EditBook;
