import React, { Component } from 'react';
import { connect } from 'react-redux';
import { NavLink } from 'react-router-dom';

import * as libraryActions from '../redux/actions/libraryActions'
import Card from 'react-bootstrap/Card';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

class Book extends Component {

    // book={book} to={'/edit/' + book.id}

    componentDidMount() {
        // fetch('http://localhost:3001')
        // .then(response => response.json())
        // .then( data => {
        //     // console.log(data)
        //     this.setState({
        //         books: [ ...data]
        //     })  
        // })
        this.props.handleListBooks(this.props.books, this.props.language)
    }


    render() {
        console.log('filteredbooklang', this.props.filteredBooks)
        console.log('this.props.books', this.props.books)
        // console.log('this.props.language', this.props.language)
        // console.log('listBookfunctie', this.props.handleListBooks(this.props.books, this.props.language))



        return (
            < Container >
                {

                    this.props.filteredBooks.map((book, i) => {
                        return (
                            <Row key={i}>
                                <Col lg={4} sm={6}>
                                    <Card >
                                        <Card.Body>
                                            <Card.Text >
                                                {book.title}
                                            </Card.Text>
                                        </Card.Body>
                                        <NavLink to={{
                                            pathname: `/edit/${book.id}`,
                                            bookProps: book
                                        }}>
                                            <Card.Img variant="bottom" src={`../img/${book.img}`} />
                                        </NavLink>
                                    </Card>
                                </Col>
                            </Row>
                        )
                    })
                }
            </Container >
        )   // closing return function
    } // closing render function

} // closing Book class

const mapStateToProps = state => {
    console.log(state)
    return {
        books: state.library.books,
        filteredBooks: state.library.filteredBooksbyLanguage
    }
}

const mapDispatchToProps = dispatch => {
    return {

        handleListBooks: (books, language) => { dispatch(libraryActions.listBooks(books, language)) }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Book);
